// Helper function to determine alert level based on risk score fun determineAlertLevel(riskScore: Number) = if (riskScore >= 8) "CRITICAL" else if (riskScore >= 6) "EMERGENCY" else "WARNING"
// Helper function to generate alert trigger condition fun generateTriggerCondition(station) = if (station.airQuality.aqi > 200) "AQI exceeded 200 (Very Unhealthy threshold)" else if (station.airQuality.aqi > 150) "AQI exceeded 150 (Unhealthy threshold)" else if (station.weather.visibility < 3) "Visibility dropped below 3km" else if (station.riskAssessment.riskScore >= 8) "Critical environmental conditions detected" else "Environmental threshold exceeded"
// Helper function to get coordination stakeholders fun getCoordinationStakeholders(alertLevel: String, location: String) = if (alertLevel == "CRITICAL") [ "EPA Emergency Response Team", "State Environmental Agency", "Local Emergency Management", "Public Health Department", location ++ " Environmental Services" ] else if (alertLevel == "EMERGENCY") [ "Environmental Response Team", "Public Health Officials", "Emergency Services" ] else [ "Environmental Monitoring Team", "Facility Management" ]
// Helper function to generate action items based on alert type fun generateActionItems(station, alertLevel: String) = if (alertLevel == "CRITICAL") [ "Issue public health advisory immediately", "Activate emergency response protocol", "Notify vulnerable population centers", "Deploy emergency environmental services", "Coordinate with " ++ station.city ++ " emergency services" ] else if (alertLevel == "EMERGENCY") [ "Monitor conditions closely", "Prepare public notifications", "Alert environmental response teams", "Check backup monitoring systems" ] else [ "Continue monitoring", "Review threshold settings", "Check equipment calibration" ]
// Filter stations that need emergency alerts (risk score >= 5) var alertStations = payload.data filter ($.riskAssessment.riskScore >= 5)
--- { alerts: alertStations map { alertId: "ENV_ALERT_" ++ $.stationId ++ "_" ++ now() as String {format: "yyyyMMdd_HHmmss"}, facilityId: $.stationId replace "CENTRAL" with "FAC", // Map to facility ID facilityName: $.city ++ " Environmental Monitoring Facility", emergencyType: if ($.airQuality.aqi > 150) "CONTAMINATION_EVENT" else if ($.weather.visibility < 5) "WEATHER_EMERGENCY" else "ENVIRONMENTAL_EXCEEDANCE", alertLevel: determineAlertLevel($.riskAssessment.riskScore), environmentalRiskScore: $.riskAssessment.riskScore, affectedPopulation: if ($.city == "New York") 125000 else if ($.city == "London") 200000 else if ($.city == "Beijing") 300000 else 50000, triggerCondition: generateTriggerCondition($), publicHealthImpact: { healthRisk: if ($.riskAssessment.riskScore >= 8) "HIGH" else if ($.riskAssessment.riskScore >= 6) "MEDIUM" else "LOW", vulnerablePopulations: [ "Children under 5", "Elderly residents", "Individuals with respiratory conditions" ], recommendedActions: if ($.airQuality.aqi > 150) [ "Stay indoors", "Use air purifiers", "Avoid outdoor exercise", "Seek medical attention if symptoms occur" ] else [ "Limit outdoor activities", "Monitor air quality updates" ] }, regulatoryCoordination: { agenciesNotified: [ "EPA Region " ++ (if ($.city == "New York") "2" else "1"), "State Environmental Department", "Local Emergency Management" ], reportingRequirements: [ { agency: "EPA", requirement: "Immediate notification within 1 hour", deadline: (now() + |PT1H|) as String {format: "yyyy-MM-dd'T'HH:mm:ss'Z'"}, status: "PENDING" } ] }, emergencyCoordinationWorkflow: { workflowId: "ENV_WF_" ++ now() as String {format: "yyyyMMdd_HHmmss"}, multiAgencyMeetingScheduled: $.riskAssessment.coordinationRequired, estimatedMeetingTime: if ($.riskAssessment.coordinationRequired) (now() + |PT30M|) as String {format: "yyyy-MM-dd'T'HH:mm:ss'Z'"} else null, coordinatingAgencies: getCoordinationStakeholders(determineAlertLevel($.riskAssessment.riskScore), $.city), emergencyActionItems: generateActionItems($, determineAlertLevel($.riskAssessment.riskScore)), slotifyIntegration: { schedulingEnabled: $.riskAssessment.coordinationRequired, meetingRoom: "Environmental Ops Center", videoConference: "https://slotify.io/env-meet-" ++ now() as String {format: "yyyyMMddHHmmss"}, participantNotifications: $.riskAssessment.coordinationRequired } }, publicNotification: { publicAlertIssued: $.riskAssessment.riskScore >= 7, notificationChannels: if ($.riskAssessment.riskScore >= 7) [ "Emergency Alert System", "Local Media", "Social Media", "Municipal Website", "Mobile Push Notifications" ] else [ "Environmental Monitoring Website", "Email Subscribers" ], affectedAreas: [ $.city ++ " Downtown", $.city ++ " Residential Areas", $.city ++ " Industrial Zone" ], communityResources: if ($.riskAssessment.riskScore >= 7) [ "Emergency shelters with air filtration", "Community health centers", "Environmental information hotline" ] else [] }, timestamp: now() as String {format: "yyyy-MM-dd'T'HH:mm:ss'Z'"} }, summary: { totalAlerts: sizeOf(alertStations), criticalAlerts: sizeOf(alertStations filter ($.riskAssessment.riskScore >= 8)), emergencyAlerts: sizeOf(alertStations filter ($.riskAssessment.riskScore >= 6 and $.riskAssessment.riskScore < 8)), warningAlerts: sizeOf(alertStations filter ($.riskAssessment.riskScore >= 5 and $.riskAssessment.riskScore < 6)), facilitiesAffected: sizeOf(alertStations), coordinationMeetingsScheduled: sizeOf(alertStations filter ($.riskAssessment.coordinationRequired)) }, timestamp: now() as String {format: "yyyy-MM-dd'T'HH:mm:ss'Z'"} }

